import { useState, useEffect } from 'react';
import './App.css';

// `https://yts.mx/api/v2/list_movies.json?minimum_rating=8.8&sort_by=year`

function App() {
  const [loading, setLoading] = useState(true)
  const [movies, setMovies] = useState([])
  const getMovies = async()=>{
    const data = await (await fetch(`https://yts.mx/api/v2/list_movies.json?minimum_rating=8.8&sort_by=year`)).json()
    setMovies(data.data.movies)
    setLoading(false)
  }

  useEffect(()=>{
    getMovies()
    console.log(movies)
  }, [])

  return (
    <div className="App">
      {loading ? <h1>Loading...</h1> : 
        <div>
          {movies.map(movie=>(
          <div key={movie.id}>
            <h2>{movie.title}</h2>
            <p>{movie.summary}</p>
            <ul>
              {movie.genres.map(genre=><li>{genre}</li>)}
            </ul>
          </div>))}
        </div>}
    </div>
  );
}

export default App;
