import { useState, useEffect } from 'react';
import '../home.css';
import Movie from '../components/Movie'

function Home(){
    const [loading, setLoading] = useState(true)
    const [movies, setMovies] = useState([])
    const getMovies = async()=>{
      const data = await (await fetch(`https://yts.mx/api/v2/list_movies.json?minimum_rating=8.9&sort_by=year`)).json()
      setMovies(data.data.movies)
      setLoading(false)
    }
  
    useEffect(()=>{
      getMovies()
    }, [])
    
    return (
      <div className="Home">
        <div className='header'>
          <h1 className='title'>Top Rated Movies,<span>and more.</span></h1>
        </div>
        {loading ? <h1>Loading...</h1> : 
          <div>
            {movies.map(movie=> 
              <Movie key={movie.id}
                     id={movie.id}
                     coverImg={movie.medium_cover_image} 
                     title={movie.title} 
                     summary={movie.summary} 
                     genres={movie.genres} />)}
  
          </div>}
      </div>
    );

}

export default Home
