import React from "react";
import PropTypes from "prop-types"
import { Link } from 'react-router-dom'
import '../home.css'

function Movie( {id, coverImg, title, genres} ) {
    return (
    <div className="item">
        <img src={coverImg} alt={title}/>
        <h2>
            <Link className='item-detail-link' 
                  to={`/movie/${id}`} 
                  style={{textDecoration:'none'}}>
                    {title}
                    {/* {title.length <25 ? title : title.slice(0,24)+"..."} */}
            </Link>
        </h2>
        <ul className="item_genres">
            {/* only show first 3 genres in genres arr */}
            {genres.filter((genre, i)=>i<3).map(genre=><li>{genre}</li>)}
        </ul>
    </div>)
}

Movie.propTypes = {
    id: PropTypes.number.isRequired,
    coverImg: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    genres: PropTypes.arrayOf(PropTypes.string).isRequired
}

export default Movie
