import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import '../css/detail.css';

function Detail() {
    const {id} = useParams()
    const [loading, setLoading] = useState(true)
    const [movie, setMovie] = useState({})
    console.log(movie)

    const getMovie= async()=>{
        const data = await (await fetch(`https://yts.mx/api/v2/movie_details.json?movie_id=${id}`)).json()
        setMovie(data.data.movie)
        setLoading(false)
    }
    useEffect(() => {
        getMovie()
    }, [])
    
    return  <>
                {loading ? <iframe src="https://giphy.com/embed/3o7bu3XilJ5BOiSGic" frameBorder="0" className='loading_icon' allowFullScreen></iframe> : 
                <div className='detail'>
                    <span className='close-btn'>&#8592;</span>
                    <img src={movie.medium_cover_image}></img>
                    <div>other info...</div>
                </div>}
            </> 
}

export default Detail
