import { useState, useEffect } from 'react';
import './App.css';
import Movie from './Movie';

function App() {
  const [loading, setLoading] = useState(true)
  const [movies, setMovies] = useState([])
  const getMovies = async()=>{
    const data = await (await fetch(`https://yts.mx/api/v2/list_movies.json?minimum_rating=8.9&sort_by=year`)).json()
    setMovies(data.data.movies)
    setLoading(false)
  }

  useEffect(()=>{
    getMovies()
    console.log(movies)
  }, [])

  return (
    <div className="App">
      {loading ? <h1>Loading...</h1> : 
        <div>
          {movies.map(movie=> 
            <Movie coverImg={movie.medium_cover_image} />)}
        </div>}
    </div>
  );
}

export default App;
