import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import '../css/detail.css';

function Detail() {
    const {id} = useParams()
    const [loading, setLoading] = useState(true)
    const [movie, setMovie] = useState({})
    console.log(movie)

    const getMovie= async()=>{
        const data = await (await fetch(`https://yts.mx/api/v2/movie_details.json?movie_id=${id}`)).json()
        setMovie(data.data.movie)
        setLoading(false)
    }
    useEffect(() => {
        getMovie()
    }, [])
    
    return  <>
                {loading ? <iframe src="https://giphy.com/embed/3o7bu3XilJ5BOiSGic" frameBorder="0" className='loading_icon' allowFullScreen></iframe> : 
                <div className='detail'>
                    <span className='close-btn'>&times;</span>
                    <div>
                        <img className='detail-img' src={movie.large_cover_image} />
                    </div>
                     <div className='detail-info'>
                        <div className='detail-info-title-and-rating'>
                            <h2 className='movie-title'>Title will be here</h2>
                            <span className='movie-rating'>*****</span>  
                        </div>
                        <div className='detail-info-genre-and-runtime'>
                            <h5 className='movie-genre'>genre arr</h5>
                            <h5 className='movie-runtime'>runtime</h5>  
                        </div>
                        <div className='detail-info-summary'>
                            summary blah blah
                        </div>
                    </div>
                </div>}
            </> 
}

export default Detail
